// src/pages/MovieDetailPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './MovieDetailPage.css';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [trailer, setTrailer] = useState('');
  const [popularity, setPopularity] = useState('');
  const posterRef = useRef(null);
  const trailerRef = useRef(null);

  useEffect(() => {
    // Fetch movie details from OMDb API
    axios.get(`https://www.omdbapi.com/?apikey=2d5488dc&i=${id}`)
      .then(response => {
        setMovie(response.data);
        fetchPopularity(response.data.imdbID); // Fetch popularity using the movie's IMDb ID
        fetchTrailer(response.data.Title);
      })
      .catch(error => console.error('Error fetching movie details:', error));
  }, [id]);

  useEffect(() => {
    if (posterRef.current && trailerRef.current) {
      const trailerHeight = trailerRef.current.clientHeight;
      posterRef.current.style.height = `${trailerHeight}px`;
    }
  }, [trailer]);

  const fetchTrailer = (title) => {
    // Fetch the trailer from YouTube
    const query = `${title} trailer`;
    const apiKey = 'AIzaSyAkHmYrSqjzt2ukp38OMIwSQ79J4PRjeZw'; // Replace with your YouTube API key
    axios.get(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${query}&key=${apiKey}`)
      .then(response => {
        if (response.data.items.length > 0) {
          setTrailer(`https://www.youtube.com/embed/${response.data.items[0].id.videoId}`);
        }
      })
      .catch(error => console.error('Error fetching trailer:', error));
  };

  const fetchPopularity = (imdbID) => {
    // Fetch popularity from your API
    axios.get(`https://yourapi.com/popularity?imdbID=${imdbID}`) // Replace with your actual API endpoint
      .then(response => {
        if (response.data && response.data.popularity) {
          setPopularity(response.data.popularity);
        }
      })
      .catch(error => console.error('Error fetching popularity:', error));
  };

  if (!movie) return <div>Loading...</div>;

  return (
    <div className="movie-detail-container">
      <h1 className="movie-title">
        {movie.Title}
        <span className="movie-rating">({movie.imdbRating}/10)</span>
        {popularity && <span className="movie-popularity">Popularity: {popularity}</span>}
      </h1>
      <div className="movie-content">
        <div className="movie-poster" ref={posterRef}>
          <img src={movie.Poster} alt={`${movie.Title} poster`} />
        </div>
        <div className="movie-trailer">
          {trailer ? (
            <iframe 
              id="trailer-iframe"
              ref={trailerRef}
              src={trailer} 
              title="YouTube video player" 
              frameBorder="0" 
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
              allowFullScreen
            ></iframe>
          ) : (
            <p>Loading trailer...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieDetailPage;
