import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Header = () => {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      setIsLoggedIn(true);
      setUsername(user.email); // Assuming email is the username
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    setUsername('');
    navigate('/login');
  };

  const handleLanguageChange = (lang) => {
    i18n.changeLanguage(lang);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <Link className="navbar-brand" to="/">{t('home')}</Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav mr-auto">
          {isLoggedIn ? (
            <>
              <li className="nav-item">
                <span className="nav-link">Hello, {username}</span>
              </li>
              <li className="nav-item">
                <button className="nav-link btn btn-link" onClick={handleLogout}>Logout</button>
              </li>
            </>
          ) : (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/login">{t('login')}</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/register">{t('register')}</Link>
              </li>
            </>
          )}
        </ul>
        <div>
          <button className="btn btn-outline-light" onClick={() => handleLanguageChange('en')}>EN</button>
          <button className="btn btn-outline-light ml-2" onClick={() => handleLanguageChange('tr')}>TR</button>
        </div>
      </div>
    </nav>
  );
};

export default Header;
