// src/i18n.js
import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

const resources = {
  en: {
    translation: {
      "home": "Home",
      "login": "Login",
      "search": "Search",
      "topMovies": "Top 10 on IMDb this week",
      "noMovies": "No movies to display.",
      "watchTrailer": "Watch Trailer"
    }
  },
  tr: {
    translation: {
      "home": "Ana Sayfa",
      "login": "Giriş",
      "search": "Arama",
      "topMovies": "Bu hafta IMDb'de en iyi 10",
      "noMovies": "Gösterilecek film yok.",
      "watchTrailer": "Fragmanı İzle"
    }
  }
};

i18n.use(initReactI18next)
  .init({
    resources,
    lng: navigator.language.startsWith('tr') ? 'tr' : 'en',
    fallbackLng: 'en',
    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
