import React, { useState, useEffect, useRef, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchMovieDetails, fetchTrailer, fetchPopularity, searchMovies, addToWatchlist, removeFromWatchlist, getWatchlist } from '../services/movieService';
import { AuthContext } from '../context/AuthContext';
import './MovieDetailPage.css';

const MovieDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isLoggedIn } = useContext(AuthContext);
  const [movie, setMovie] = useState(null);
  const [trailer, setTrailer] = useState('');
  const [popularity, setPopularity] = useState('');
  const [searchText, setSearchText] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isInWatchlist, setIsInWatchlist] = useState(false);
  const posterRef = useRef(null);
  const trailerRef = useRef(null);

  useEffect(() => {
    const loadMovieDetails = async () => {
      const movieData = await fetchMovieDetails(id);
      setMovie(movieData);
      const trailerUrl = await fetchTrailer(movieData.Title);
      setTrailer(trailerUrl);
      const popularityData = await fetchPopularity(movieData.imdbID);
      setPopularity(popularityData);
      if (isLoggedIn) {
        const watchlist = await getWatchlist();
        setIsInWatchlist(watchlist.some(item => item.imdbID === movieData.imdbID));
      }
    };
    loadMovieDetails();
  }, [id, isLoggedIn]);

  useEffect(() => {
    if (posterRef.current && trailerRef.current) {
      const trailerHeight = trailerRef.current.clientHeight;
      posterRef.current.style.height = `${trailerHeight}px`;
    }
  }, [trailer]);

  const handleSearch = async (e) => {
    const query = e.target.value;
    setSearchText(query);
    if (query.length >= 3) {
      const results = await searchMovies(query);
      setSearchResults(results);
    } else {
      setSearchResults([]);
    }
  };

  const handleResultClick = (imdbID) => {
    setSearchText('');
    setSearchResults([]);
    navigate(`/movie/${imdbID}`);
  };

  const handleSearchSubmit = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      navigate(`/search?query=${searchText}`);
    }
  };

  const handleAddToWatchlist = async () => {
    if (!isLoggedIn) {
      navigate('/login');
    } else {
      await addToWatchlist(id);
      setIsInWatchlist(true);
    }
  };

  const handleRemoveFromWatchlist = async () => {
    await removeFromWatchlist(id);
    setIsInWatchlist(false);
  };

  if (!movie) return <div>Loading...</div>;

  return (
    <div className="movie-detail-container">
      <div className="search-bar">
        <input
          type="text"
          className="form-control"
          value={searchText}
          onChange={handleSearch}
          onKeyDown={handleSearchSubmit}
          placeholder="Search movies..."
        />
        {searchResults.length > 0 && (
          <div className="dropdown-menu show">
            {searchResults.map((result) => (
              <div
                className="dropdown-item d-flex align-items-center"
                key={result.imdbID}
                onClick={() => handleResultClick(result.imdbID)}
              >
                <img
                  src={result.Poster}
                  alt={result.Title}
                  style={{ width: '50px', height: '75px', marginRight: '10px' }}
                />
                <span>{result.Title} - {result.Year}</span>
              </div>
            ))}
          </div>
        )}
      </div>
      <h1 className="movie-title">
        {movie.Title}
      </h1>
      <h2 className="info">
        <span className="movie-rating">IMDb Rating⭐({movie.imdbRating}/10)</span>
        {popularity && <span className="movie-popularity">Popularity: {popularity}</span>}
      </h2>
      {isLoggedIn && (
        <div className="watchlist-button">
          {isInWatchlist ? (
            <button onClick={handleRemoveFromWatchlist}>Remove from my watchlist</button>
          ) : (
            <button onClick={handleAddToWatchlist}>Add to my watchlist</button>
          )}
        </div>
      )}
      <div className="movie-content">
        <div className="movie-poster" ref={posterRef}>
          <img src={movie.Poster} alt={`${movie.Title} poster`} />
        </div>
        <div className="movie-trailer">
          {trailer ? (
            <iframe
              id="trailer-iframe"
              ref={trailerRef}
              src={trailer}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          ) : (
            <p>Loading trailer...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieDetailPage;
