// src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieSlider from '../components/MovieSlider';
import { useTranslation } from 'react-i18next';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [searchText, setSearchText] = useState('');
  const { t } = useTranslation();

  useEffect(() => {
    // Fetch initial 10 movies for the slider
    axios.get(`https://www.omdbapi.com/?apikey=2d5488dc&s=movie`)
      .then(response => {
        console.log('Movies:', response.data);
        if (response.data.Search) {
          const moviePromises = response.data.Search.slice(0, 10).map(movie =>
            axios.get(`https://www.omdbapi.com/?apikey=2d5488dc&i=${movie.imdbID}`)
          );
          Promise.all(moviePromises)
            .then(movieResponses => {
              const moviesWithRatings = movieResponses.map(res => res.data);
              setMovies(moviesWithRatings);
            });
        } else {
          console.error('No movies found');
        }
      })
      .catch(error => console.error('Error fetching movies:', error));
  }, []);

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchText(query);
    if (query.length >= 3) {
      axios.get(`https://www.omdbapi.com/?s=${query}&apikey=2d5488dc`)
        .then(response => {
          console.log('Search results:', response.data);
          if (response.data.Search) {
            setSearchResults(response.data.Search.slice(0, 3));
          } else {
            setSearchResults([]);
          }
        })
        .catch(error => console.error('Error fetching search results:', error));
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div className="container mt-4">
      <h1>{t('home')}</h1>
      <div className="form-group">
        <input 
          type="text" 
          className="form-control" 
          value={searchText} 
          onChange={handleSearch} 
          placeholder={t('search')} 
        />
        {searchResults.length > 0 && (
          <div className="dropdown-menu show">
            {searchResults.map((result) => (
              <div className="dropdown-item" key={result.imdbID}>
                {result.Title} - {result.Year}
              </div>
            ))}
          </div>
        )}
      </div>
      {movies.length > 0 ? (
        <MovieSlider movies={movies} />
      ) : (
        <p>No movies to display.</p>
      )}
    </div>
  );
};

export default HomePage;
