// src/components/MovieSlider.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const MovieSlider = ({ movies }) => {
  return (
    <div id="movieCarousel" className="carousel slide" data-ride="carousel">
      <div className="carousel-inner">
        {movies.map((movie, index) => (
          <div key={movie.imdbID} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
            <img src={movie.Poster} className="d-block w-100" alt={movie.Title} />
            <div className="carousel-caption d-none d-md-block">
              <h5>{movie.Title}</h5>
              <p>{movie.imdbRating}</p>
              <a href={`https://www.youtube.com/results?search_query=${movie.Title}+trailer`} target="_blank" rel="noopener noreferrer" className="btn btn-primary">Watch Trailer</a>
            </div>
          </div>
        ))}
      </div>
      <a className="carousel-control-prev" href="#movieCarousel" role="button" data-slide="prev">
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="sr-only">Previous</span>
      </a>
      <a className="carousel-control-next" href="#movieCarousel" role="button" data-slide="next">
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="sr-only">Next</span>
      </a>
    </div>
  );
};

export default MovieSlider;
