import React from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Header = () => {
  const { t, i18n } = useTranslation();

  const handleLanguageChange = (lang) => {
    i18n.changeLanguage(lang);
  };

  const isLoggedIn = false; // Replace with actual authentication check, e.g., localStorage.getItem('token')

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <Link className="navbar-brand" to="/">{t('home')}</Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav mr-auto">
          {!isLoggedIn && (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/login">{t('login')}</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/register">{t('register')}</Link>
              </li>
            </>
          )}
          {isLoggedIn && (
            <li className="nav-item">
              <Link className="nav-link" to="/logout">{t('logout')}</Link> {/* Add a logout link if needed */}
            </li>
          )}
        </ul>
        <div>
          <button className="btn btn-outline-light" onClick={() => handleLanguageChange('en')}>EN</button>
          <button className="btn btn-outline-light ml-2" onClick={() => handleLanguageChange('tr')}>TR</button>
        </div>
      </div>
    </nav>
  );
};

export default Header;
