// src/components/Header.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Header = () => {
  const { t, i18n } = useTranslation();

  const handleLanguageChange = (lang) => {
    i18n.changeLanguage(lang);
  };

  const isLoggedIn = false; // Replace with actual authentication check

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <Link className="navbar-brand" to="/">{t('home')}</Link>
      <div className="collapse navbar-collapse">
        <ul className="navbar-nav mr-auto">
          <li className="nav-item">
            {isLoggedIn ? null : <Link className="nav-link" to="/login">{t('login')}</Link>}
          </li>
        </ul>
        <button className="btn btn-outline-light" onClick={() => handleLanguageChange('en')}>EN</button>
        <button className="btn btn-outline-light ml-2" onClick={() => handleLanguageChange('tr')}>TR</button>
      </div>
    </nav>
  );
};

export default Header;
