// src/components/MovieSlider.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const MovieSlider = ({ movies }) => {
  return (
    <div className="container mt-4">
      <h2>Top 10 on IMDb this week</h2>
      <div className="row">
        {movies.map((movie, index) => (
          <div key={movie.imdbID} className="col-md-4 col-lg-3 mb-4">
            <div className="card">
              <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
              <div className="card-body">
                <h5 className="card-title">{index + 1}. {movie.Title}</h5>
                <p className="card-text">‚≠ê {movie.imdbRating}</p>
                <a href={`https://www.youtube.com/results?search_query=${movie.Title}+trailer`} target="_blank" rel="noopener noreferrer" className="btn btn-primary">Watch Trailer</a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieSlider;
