// src/pages/MovieDetailPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './MovieDetailPage.css';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [trailer, setTrailer] = useState('');

  useEffect(() => {
    // Fetch movie details from OMDb API
    axios.get(`https://www.omdbapi.com/?apikey=2d5488dc&i=${id}`)
      .then(response => {
        setMovie(response.data);
        fetchTrailer(response.data.Title);
      })
      .catch(error => console.error('Error fetching movie details:', error));
  }, [id]);

  const fetchTrailer = (title) => {
    // Fetch the trailer from YouTube
    const query = `${title} trailer`;
    const apiKey = 'YOUR_YOUTUBE_API_KEY'; // Replace with your YouTube API key
    axios.get(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${query}&key=${apiKey}`)
      .then(response => {
        if (response.data.items.length > 0) {
          setTrailer(`https://www.youtube.com/embed/${response.data.items[0].id.videoId}`);
        }
      })
      .catch(error => console.error('Error fetching trailer:', error));
  };

  if (!movie) return <div>Loading...</div>;

  return (
    <div className="movie-detail-container">
      <div className="movie-poster">
        <img src={movie.Poster} alt={`${movie.Title} poster`} className="img-fluid" />
      </div>
      <div className="movie-info">
        <h1 className="movie-title">{movie.Title} <span className="movie-rating">({movie.imdbRating}/10)</span></h1>
        <div className="movie-year">
          {movie.Year} • {movie.Rated} • {movie.Runtime}
        </div>
        <div className="movie-trailer">
          {trailer ? (
            <iframe 
              width="100%" 
              height="315" 
              src={trailer} 
              title="YouTube video player" 
              frameBorder="0" 
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
              allowFullScreen
            ></iframe>
          ) : (
            <p>Loading trailer...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieDetailPage;
