// src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieSlider from '../components/MovieSlider';
import { useTranslation } from 'react-i18next';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [searchText, setSearchText] = useState('');
  const { t } = useTranslation();

  useEffect(() => {
    // Fetch initial 10 movies for the slider
    axios.get(`https://www.omdbapi.com/?s=movie&apikey=YOUR_OMDB_API_KEY`)
      .then(response => setMovies(response.data.Search.slice(0, 10)))
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchText(query);
    if (query.length >= 3) {
      axios.get(`https://www.omdbapi.com/?s=${query}&apikey=YOUR_OMDB_API_KEY`)
        .then(response => setSearchResults(response.data.Search.slice(0, 3)))
        .catch(error => console.error(error));
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div>
      <h1>{t('home')}</h1>
      <div>
        <input type="text" value={searchText} onChange={handleSearch} placeholder={t('search')} />
        {searchResults.length > 0 && (
          <div className="search-dropdown">
            {searchResults.map((result) => (
              <div key={result.imdbID}>
                {result.Title} - {result.Year}
              </div>
            ))}
          </div>
        )}
      </div>
      <MovieSlider movies={movies} />
    </div>
  );
};

export default HomePage;
